@constraint(model, fat_lower_bound[d=1:D], sum(fat[r]*x[r,d,p] for r in 1:R for p in 1:P) .>= fat_l)

@constraint(model, protein_upper_bound[d=1:D], sum(protein[r]*x[r,d,p] for r in 1:R for p in 1:P) - protein_u .== pos[4,d] - neg[4,d])
@constraint(model, protein_lower_bound[d=1:D], sum(protein[r]*x[r,d,p] for r in 1:R for p in 1:P) .>= protein_l)

# variation constraints
@constraint(model, no_repeat_week[r=1:R], sum(x[r,d,p] for d in 1:D for p in 1:P) .<= 2)  # no repeat dishes in a week

# budget constraint
@constraint(model, budget[d=1:D], sum(price[r] * x[r,d,p] for r in 1:R for p in 1:P) .<= B)  # daily budget

# schedule constraint
@constraint(model, schedue[p=1:P,d=1:D],sum(x[r,d,p]*cooking[r] for r in 1:R) .<= 90)

# Glide Path
@constraint(model,day1[d=1:D,p=1:P,r=[143,163,234,61,134,202,230,39,64,113,203]],x[r,d,p]==0)
@constraint(model,day2[d=1:D,p=1:P,r=[99,137,9,29,181,201,90,93,101,117,152]],x[r,d,p]==0)
@constraint(model,day3[d=1:D,p=1:P,r=[1,56,68,17,189,232,6,75,185]],x[r,d,p]==0)
@constraint(model,day4[d=1:D,p=1:P,r=[3,120,162,73,78,81,97,114]],x[r,d,p]==0)

# objective
@objective(model, Max, 
sum(rating[p,r]*x[r,d,p] for d in 1:D for r in 1:R for p in 1:P) -
sum(beta[i]*pos[i,d] for i in 1:4 for d in 1:D)
)

# call optimizer
optimize!(model)

@show objective_value(model)
@show sum(value.(x))
@show value.(x[:,:,1])
@show value.(x[:,:,2])
@show value.(x[:,:,3])

@show value.(sum(price[r] * x[r,d,p] for r in 1:R for p in 1:P for d in 1:D))



